import axios from "axios";
import React, { useEffect, useState } from "react";
import "./users.css"; // Custom CSS for styling


const Users = () => {
  const [users, setUsers] = useState([]);

  // Fetch all users
  useEffect(() => {
    axios
      .get("http://localhost:8000/users") // Replace with your backend URL
      .then((response) => {
        setUsers(response.data);
      })
      .catch((error) => {
        console.error("Error fetching users:", error);
      });
  }, []);

  // Block a user
  const blockUser = (userId) => {
    axios
      .post(`http://localhost:8000/users/${userId}`, { action: "block" })
      .then(() => {
        alert("User blocked successfully!");
        setUsers(users.map((u) => (u._id === userId ? { ...u, subscribed: false } : u)));
      })
      .catch((error) => {
        console.error("Error blocking user:", error);
      });
  };

  // Delete a user
  const deleteUser = (userId) => {
    axios
      .post(`http://localhost:8000/users/${userId}`, { action: "delete" })
      .then(() => {
        alert("User deleted successfully!");
        setUsers(users.filter((u) => u._id !== userId));
      })
      .catch((error) => {
        console.error("Error deleting user:", error);
      });
  };

  return (
    <div>
      <h2>User Management</h2>
      <table>
        <thead>
          <tr>
            <th>Chat ID</th>
            <th>Subscribed</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {users.map((user) => (
            <tr key={user._id}>
              <td>{user.chatId}</td>
              <td>{user.subscribed ? "Yes" : "No"}</td>
              <td>
                <button onClick={() => blockUser(user._id)}>Block</button>
                <button onClick={() => deleteUser(user._id)}>Delete</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default Users;
